import React, { FC, useState, useEffect, Fragment } from "react";
import MaterialTable from "material-table";
import { getListAndItems } from "Components";
import { Icons } from "Components";

interface TableProps {
  listName: string;
  tableTitle?: string;
  options?: Object;
  changeItemPermission?: boolean;
  customActions?: Array<Object>;
}

export const Table: FC<TableProps> = ({ listName, options, tableTitle }) => {
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [title, setTitle] = useState("");
  const [actions, setActions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const populateTable = async () => {
    //@ts-ignore
let _spPageContextInfo = window._spPageContextInfo;
let APIurl = "";
if (_spPageContextInfo !== undefined) {
  //@ts-ignore
  APIurl = _spPageContextInfo.webServerRelativeUrl;
} else {
  APIurl = "http://localhost:8081/";
}
    setIsLoading(true);

    const list = await getListAndItems(listName);

    if (tableTitle) {
      setTitle(tableTitle);
    } else {
      setTitle(list.title);
    }

    setColumns(list.columns);
    setData(list.items);

    setIsLoading(false);
  };

  useEffect(() => {
    populateTable();
    return () => {};
  }, []);

  return (
    <>
      <MaterialTable columns={columns} data={data} title={title} options={options} actions={actions} isLoading={isLoading} />
    </>
  );
};
