import React, { FC, useState, useEffect, Fragment } from "react";
import MaterialTable from "material-table";
import { getListAndItems } from "Components";
import { Icons } from "Components";

interface TableProps {
  listName: string;
  tableTitle?: string;
  isDirty?: boolean;
  options?: Object;
  addItem?: boolean;
  addOptions?: Object;
  deleteItem?: boolean;
  editItem?: boolean;
  changeItemPermission?: boolean;
  customActions?: Array<Object>;
  handleDirty?: Function;
}

export const Table: FC<TableProps> = ({
  listName,
  addItem = true,
  addOptions = {
    title: "Add Item",
    content: "Content",
    saveButtonText: "Save",
    saveAction: () => {
      console.log("I am saved");
    },
    cancelButtonText: "Cancel",
    cancelAction: () => {
      console.log("I am lost");
    },
  },
  deleteItem = true,
  editItem = true,
  changeItemPermission = true,
  customActions,
  options,
  isDirty = true,
  handleDirty = (dirty: boolean) => {
    console.log(`handleDirty Default has been passed '${dirty}'`);
  },
  tableTitle,
}) => {
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [title, setTitle] = useState("");
  const [actions, setActions] = useState([]);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [dialogTitle, setDialogTitle] = useState();
  const [dialogContent, setDialogContent] = useState();
  const [dialogSaveButtonText, setDialogSaveButtonText] = useState();
  const [dialogSaveAction, setDialogSaveAction] = useState();
  const [dialogCancelButtonText, setDialogCancelButtonText] = useState();
  const [dialogCancelAction, setDialogCancelAction] = useState();

  const saveButtonHandler = (results: Object) => {
    //@ts-ignore
    addOptions.saveAction(results);
    handleDirty(true);
    setDialogOpen(false);
  };

  const cancelButtonHandler = (results: Object) => {
    //@ts-ignore
    addOptions.cancelAction(results);
    setDialogOpen(false);
  };

  useEffect(() => {
    if (addItem) {
      setActions((prevActions) => {
        prevActions.push({
          //@ts-ignore
          icon: Icons.Add,
          //@ts-ignore
          tooltip: "Add Item",
          //@ts-ignore
          isFreeAction: true,
          //@ts-ignore
          onClick: (event, rowdata) => {
            //@ts-ignore
            setDialogTitle(addOptions.title);
            //@ts-ignore
            setDialogContent(addOptions.content);
            //@ts-ignore
            setDialogSaveButtonText(addOptions.saveButtonText);
            //@ts-ignore
            setDialogCancelButtonText(addOptions.cancelButtonText);
            //@ts-ignore
            setDialogCancelAction(() => {
              return () => {
                setIsLoading(false);
                //@ts-ignore
                addOptions.cancelAction();
              };
            });
            setDialogOpen(true);
          },
        });

        return prevActions;
      });
    }

    if (deleteItem) {
      setActions((prevActions) => {
        prevActions.push({
          //@ts-ignore
          icon: Icons.Delete,
          //@ts-ignore
          tooltip: "Delete Item",
          //@ts-ignore
          onClick: (event, rowdata) => {
            //TODO: delete item actions
          },
        });

        return prevActions;
      });
    }

    if (editItem) {
      setActions((prevActions) => {
        prevActions.push({
          //@ts-ignore
          icon: Icons.Edit,
          //@ts-ignore
          tooltip: "Edit Item",
          //@ts-ignore
          onClick: (event, rowdata) => {
            //TODO: edit item actions
          },
        });

        return prevActions;
      });
    }

    if (changeItemPermission) {
      setActions((prevActions) => {
        prevActions.push({
          //@ts-ignore
          icon: Icons.People,
          //@ts-ignore
          tooltip: "Change Item Permissions",
          //@ts-ignore
          onClick: (event, rowdata) => {
            //TODO: change item permissions actions
          },
        });

        return prevActions;
      });
    }

    if (customActions) {
      customActions.map((action, index) => {
        //@ts-ignore
        action.icon = Icons[action.icon];

        return setActions((prevActions) => {
          //@ts-ignore
          prevActions.push(action);
          return prevActions;
        });
      });
    }
    return () => {};
  }, []);

  const populateTable = async () => {
    setIsLoading(true);

    const list = await getListAndItems(listName);

    if (tableTitle) {
      setTitle(tableTitle);
    } else {
      setTitle(list.title);
    }

    setColumns(list.columns);
    setData(list.items);

    handleDirty(false);

    setIsLoading(false);
  };

  useEffect(() => {
    if (isDirty) {
      populateTable();
    }

    return () => {};
  }, [isDirty]);

  return (
    <>
      <MaterialTable columns={columns} data={data} title={title} options={options} actions={actions} isLoading={isLoading} />
    </>
  );
};
